class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        int n = nums.length;
        long diff[]=new long[n];
        for(int i=0;i<n;i++)
            diff[i]=target[i]-nums[i];
        boolean done[]=new boolean[n];
        long ans=0L;
        for(int i=0;i<n;i++)
        {
            if(i==0)ans+=Math.abs(diff[i]);
            else{
                if(check(diff,i))
                {
                  //  System.out.println(diff[i]+" "+diff[i-1]);

                    if(diff[i]>0)
                    {
                        if(diff[i]>diff[i-1])ans+=diff[i]-diff[i-1];
                    }
                    else{
                        if(diff[i-1]>diff[i])ans+=Math.abs(diff[i]-diff[i-1]);
                    }
                }
                else{

                    ans+=Math.abs(diff[i]);
                }

            }
           // System.out.println("ans "+ans);
        }
        return ans;
    }
    boolean check(long dif[], int index)
    {
        return (dif[index]>0 && dif[index-1]>0 ) || (dif[index]<0 && dif[index-1]<0);
    }
}
