class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n = len(nums)

        diff = [target[i] - nums[i] for i in range(n)]

        res = abs(diff[0])

        for i in range(1, n):
            if diff[i] * diff[i - 1] > 0:
                if diff[i] > 0:
                    res += max(diff[i] - diff[i - 1], 0)
                else:
                    res += max(diff[i - 1] - diff[i], 0)
            else:
                res += abs(diff[i])

        return res
