class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        differences = [target[i] - nums[i] for i in range(len(nums))]
        total_operations = 0
        previous_difference = 0

        for i in range(len(differences)):
            if i == 0 or (differences[i - 1] > 0 and differences[i] > 0) or (differences[i - 1] < 0 and differences[i] < 0):
                operation_diff = abs(differences[i]) - abs(previous_difference)
                if operation_diff > 0:
                    total_operations += operation_diff
                previous_difference = differences[i]
            else:
                total_operations += abs(differences[i])
                previous_difference = differences[i]

        return total_operations
