class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n = len(nums)
        check = [i - e for i,e in zip(target,nums)]
        result = [0] * n
        for i in range(n):
            if i == n - 1:
                result[i] += -1 * check[i]
            else:
                result[i] += -1 * check[i]
                result[i + 1] += check[i]
        return max(abs(sum([i for i in result if i < 0])),abs(sum([i for i in result if i > 0])))
