class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long count = 0;
        long long last = 0;
        for (int i = 0; i < nums.size(); i++) {
            long long diff = (long long)nums[i] - (long long)target[i];
            if (diff < 0 && last < 0)
                count += max(0ll, last - diff);
            else if (diff >= 0 && last >= 0) {
                count += max(0ll, diff - last);
            }
            else {
                count += abs(diff);
            }
            last = diff;
        }
        return count;
    }
};
