class Solution {
public:
    bool sign(long long x){
        if(x<0){
            return false;
        }

        return true;
    }
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        /*

            1 1 1 


            1 -2 +2

            1 + 2 + 2

            if(i==0) diff[i-1]=0;

            if diff[i] != diff[i-1]
            res+=abs(diff[i-1]);

            i++;



            res+=abs(diff[i-1]);

            last_action = sign
            diff_prev also  sign
            same sign
            if(abs(diff_prev)<=abs(last_action)){
                no need to increase ops
            }
            else{
                res+=(abs(diff_prev) - abs(last_action))
            }

            if diff sign

            2 2 2 1 1 1 2 2 2 2 2

            3


        */
        int n = nums.size();
        long long diff_prev =  target[0] - nums[0];
        int i=1;
        long long res = 0;
        long long last_action = 0;
        bool first = true;
        while(i<n){
            long long diff = target[i] - nums[i];

            if(diff != diff_prev){
                if(first || sign(last_action)!=sign(diff_prev)){
                    res+=abs(diff_prev);
                    last_action = diff_prev;
                    diff_prev = diff;
                    first = false;
                }
                else{
                    if(abs(diff_prev) > abs(last_action)){
                        res+=(abs(diff_prev) - abs(last_action));
                    }
                    last_action = diff_prev;
                    diff_prev = diff;                  
                }

            }
            i++;
        }
        if(first || sign(last_action)!=sign(diff_prev)){
            res+=abs(diff_prev);
            last_action = diff_prev;
            first = false;
        }
        else{
            if(abs(diff_prev) > abs(last_action)){
                res+=(abs(diff_prev) - abs(last_action));
            }
            last_action = diff_prev;                
        }
        return res;
    }
};
