class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans=0, pr=0;
        for(int i=0;i<nums.size();i++){
            long long c = target[i]-nums[i];
            if(pr<0 && c>0){
                ans += c;
            }
            else if(pr>0 && c<0){
                ans += abs(c);
            }
            else{
                long long a=abs(pr), b=abs(c);
                if(a<b){
                    ans += (b-a);
                }
            }
            pr = c;
        }
        return ans;
    }
};
