from typing import List

class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        # Compute the difference between each element in target and nums
        diffs = [target[i] - nums[i] for i in range(len(nums))]       
        total_operations = 0
        segment_difference = 0       
        for index in range(len(diffs)):
            if index == 0 or (diffs[index - 1] > 0 and diffs[index] > 0) or (diffs[index - 1] < 0 and diffs[index] < 0):
                needed_operations = abs(diffs[index]) - abs(segment_difference)
                if needed_operations > 0:
                    total_operations += needed_operations
                segment_difference = diffs[index]
            else:  
                total_operations += abs(diffs[index])
                segment_difference = diffs[index]        
        return total_operations
