class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n = len(nums)
        a = [y - x for x, y in zip(nums, target)]
        ans = 0
        stk = [0]
        for x in a:
            if x > 0:
                while stk[-1] < 0:
                    stk.pop()
                if x > stk[-1]:
                    ans += x - stk[-1]
                    stk.append(x)
                else:
                    while stk[-1] > x:
                        stk.pop()
                    stk.append(x)
            elif x < 0:
                while stk[-1] > 0:
                    stk.pop()
                if x < stk[-1]:
                    ans += stk[-1] - x
                    stk.append(x)
                else:
                    while stk[-1] < x:
                        stk.pop()
                    stk.append(x)
            else:
                stk = [0]
        return ans
