class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        map<long long int, long long int>mp;
        long long int cnt = 0;
        long long int n = nums.size();
        for(int i=0;i<n;i++){
            if(i==0){
                long long int change = target[i]-nums[i];
                mp[i]=change;
                long long int t = change;
                if(t<0){
                    t=-t;
                }
                cnt+=t;

            }
            else{
                long long int change = target[i]-nums[i];
                mp[i]=change;
                if(change>0 && mp[i-1]<0 || change<0 && mp[i-1]>0){
                    cnt+=abs(change);
                }
                else{
                    if(change>0){
                        if(change<=mp[i-1]){
                            continue;
                        }
                        else{
                            long long int d = change-mp[i-1];
                            cnt+=d;
                        }
                    }
                    else if(change<0){
                        long long int prev = abs(mp[i-1]);
                        long long int curr = abs(change);
                        if(curr<prev){
                            continue;
                        }
                        else{
                            cnt+=(curr-prev);
                        }

                    }
                }
            }

        }
        return cnt;
    }
};
