class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        long long incr = 0;
        long long count = 0;
        for(int i = 0;i<n;i++){
            long long inc = target[i] - nums[i];
            if( inc <= 0){
                // need to decrement
                if(incr > 0){
                    count+=inc*-1;
                    incr = inc;
                }
                else{
                    if(inc >= incr){
                        incr = inc;
                    }
                    else{
                        count+= incr-inc;
                        incr = inc;
                    }
                }
            }
            else{
                if(incr < 0){
                    count+= inc;
                    incr = inc;
                }
                else{
                    if(inc >= incr){
                        count+= inc - incr;
                        incr = inc;
                    }
                    else{
                        incr = inc;
                    }
                }
            }
        }
        return count;
    }
};
