class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans = 0, cur = 0;
            int n = nums.size();
            vector <int> arr(n, 0);
            for(int i = 0; i < n; i++)arr[i] = target[i]-nums[i];
            for(int i = 0; i < n; i++){
                    if(i == 0 || arr[i] < 0 && arr[i-1] <0 || arr[i] > 0 && arr[i-1] > 0){
                            if((abs(arr[i])-abs(cur)) > 0)ans += abs(arr[i])-abs(cur);
                    }
                    else ans += abs(arr[i]);
                    cur = arr[i];
            }
            return ans;
    }

};
