class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        l = []
        for i in range(len(nums)):
            l.append(target[i]-nums[i])    
        o=0
        c=0       
        for i in range(len(l)):
            if i == 0 or (l[i-1]>0 and l[i]>0) or (l[i-1]<0 and l[i]<0):
                ad =abs(l[i])-abs(c)
                if ad > 0:
                    o+=ad
                c=l[i]
            else:  
                o+=abs(l[i])
                c=l[i]        
        return o
