class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        for(int i = 0; i < nums.length; i++) {
            target[i] -= nums[i];
        }

        long total = Math.abs(target[0]);
        int min = Math.abs(target[0]);

        // System.out.println(total + ", " + min);
        for(int i = 1; i < nums.length; i++) {
            if((target[i-1] > 0 && target[i] > 0) || target[i-1] < 0 && target[i] < 0) {
                if(Math.abs(target[i-1]) < Math.abs(target[i])) {
                    // System.out.println(i + ", " + (Math.abs(target[i]) - Math.abs(target[i-1])));
                    total += (Math.abs(target[i]) - Math.abs(target[i-1]));
                }
                min = Math.min(Math.abs(target[i]), min);
            } else {
                // System.out.println(i + "," + target[i]);
                total += Math.abs(target[i]);
                min = Math.abs(target[i]);
            }
        }

        return total;
    }
}
