class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
       int p = 0, d = 0, s = 0;
        long long res = 0;
        int n = nums.size();

        for (int i = 0; i < n; ++i) {
            if (p == 0) {
                if (nums[i] < target[i]) {
                    p = 1;
                    d = target[i] - nums[i];
                    s++;
                } else if (nums[i] > target[i]) {
                    p = -1;
                    d = nums[i] - target[i];
                    s++;
                } else {
                    continue;
                }
            } else {
                if ((nums[i] < target[i] && p == 1 && d == target[i] - nums[i]) ||
                    (nums[i] > target[i] && p == -1 && d == nums[i] - target[i])) {
                    s++;
                    continue;
                } else if (nums[i] < target[i] && p == 1) {
                    if (target[i] - nums[i] > d) {
                        s = 1;
                        d = target[i] - nums[i];
                    } else {
                        res += d - target[i] + nums[i];
                        d = target[i] - nums[i];
                    }
                    continue;
                } else if (nums[i] > target[i] && p == -1) {
                    if (nums[i] - target[i] > d) {
                        s = 1;
                        d = nums[i] - target[i];
                    } else {
                        res += d + target[i] - nums[i];
                        d = nums[i] - target[i];
                    }
                    continue;
                } else if (nums[i] == target[i]) {
                    res += d;
                    p = 0;
                    s = 0;
                    d = 0;
                } else {
                    res += d;
                    if (nums[i] < target[i]) {
                        p = 1;
                        d = target[i] - nums[i];
                        s++;
                    } else {
                        p = -1;
                        d = nums[i] - target[i];
                        s++;
                    }
                }
            }
        }

        return res + d; 
    }
};
