class Solution {
public:
    long long minimumOperations(vector<int>& v, vector<int>& c) {
        long long prev=0;
        long long ans=0;
        int pos=0, neg=0;

        for(int i=0; i<v.size(); i++){
            if(c[i]==v[i]){
                pos=0; neg=0; 
                continue;
            }

            long long cur=c[i]-v[i];
            // cout<<pos<<" "<<neg<<" "<<prev<<" "<<cur<<endl;
            if(pos==1 && cur>0){ 
                if(cur>prev)ans+=(cur-prev);
                prev=cur;
            }else if(neg==1 && cur<0){
                if(cur<prev)ans+=abs(cur-prev);
                prev=cur;
            }else{ 
                pos=0; neg=0; 
                if(cur>0){pos=1;ans+=cur;}
                else {neg=1; ans-=cur;}
                prev=cur; 
            }
            // cout<<ans<<endl;
        }

        return ans;
    }
};
