class Solution {
public:
    long long minop(vector<int>&x)  
{    

    long long ans = x[0];  

    for (int i = 1; i < x.size(); i++) {    
        if(x[i]>x[i-1])
        ans += x[i] - x[i - 1];
    }  


    return ans;  
} 
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans=0;int n=nums.size();
        vector<int>dif(n);

        for(int i=0;i<n;i++){
            dif[i]=target[i]-nums[i];
        }

        int st=-1,e=-1;
        st=0;e=0;int i=1;

        while(i<=n){

            while(i<n && ((dif[i]>=0 && dif[i-1]>=0) || (dif[i]<0 && dif[i-1]<0))){
                i++;
            }
            e=i;
            //cout<<st<<" "<<e<<endl;
            if(dif[i-1]<0){
                for(int j=st;j<e;j++){
                    dif[j]=abs(dif[j]);
                }
            }

            vector<int>x(dif.begin()+st,dif.begin()+e);

            ans+=minop(x);
            st=i;
        i++;
        }


        return ans;
    }
};
