class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        int arr[] = new int[nums.length];
        long dp[] = new long[nums.length];
        for(int i = 0; i < nums.length; i++) arr[i] = target[i] - nums[i];
        dp[0] = Math.abs(arr[0]);
        for(int i = 1; i < nums.length; i++) {
            if(arr[i] < 0) {
                if(arr[i - 1] < 0) {
                    if(arr[i - 1] <= arr[i]) dp[i] = dp[i - 1];
                    else dp[i] = dp[i - 1] + Math.abs(arr[i] - arr[i - 1]);
                } else dp[i] = dp[i - 1] + Math.abs(arr[i]);
            } else {
                if(arr[i - 1] < 0) dp[i] = dp[i - 1] + arr[i];
                else {
                    if(arr[i] <= arr[i - 1]) dp[i] = dp[i - 1];
                    else if(arr[i] > arr[i - 1]) dp[i] = dp[i - 1] + arr[i] - arr[i - 1];
                }
            }
        }
        return dp[nums.length - 1];
    }
}
