class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        oper = 0
        for i in range(0, len(nums)):
            diff = target[i] - nums[i]
            oper += abs(diff)
            j = i+1
            while j < len(nums):
                diff_j = target[j] - nums[j]
                if ((diff > 0 and diff_j > 0) or (diff < 0 and diff_j < 0)):
                    if abs(diff) >= abs(diff_j):
                        nums[j] = target[j]
                        diff = diff_j
                    else:
                        nums[j] += diff
                    j += 1
                else:
                    break
        return oper
