class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<int> diff(n);
        for (int i = 0; i < n; ++i) {
            diff[i] = target[i] - nums[i];
        }
        long long total_op = 0;
        int current_diff = 0;
        for (int i = 0; i < n; ++i) {
            if (i == 0 || (diff[i - 1] > 0 && diff[i] > 0) ||
                (diff[i - 1] < 0 && diff[i] < 0)) {
                int op =
                    abs(diff[i]) - abs(current_diff);
                if (op > 0) {
                    total_op += op;
                }
                current_diff = diff[i];
            } else {
                total_op += abs(diff[i]);
                current_diff = diff[i];
            }
        }
        return total_op;
    }
};
