class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        int n = nums.length;
        for (int i = 0; i < n; i++)
            nums[i] -= target[i];
        for (int i = n - 1; i >= 1; i--)
            nums[i] -= nums[i - 1];
        long result = 0, sum = 0;
        for (int i = 0; i < n; i++) {
            result += Math.abs(nums[i]);
            sum += nums[i];
        }
        return (result - Math.abs(sum)) / 2 + Math.abs(sum);
    }
}
