class Solution {
public:
    bool pos(vector<int>& nums, vector<int>& target,long long mid){
        int flag = (nums[0]<=target[0]) ? 0 : 1;
        long long ans=abs(nums[0]-target[0]),temp;
        temp=ans;
        for(int i=1;i<nums.size();i++){
            long long diff=abs(nums[i]-target[i]);
            if(nums[i]==target[i]){
                temp=0;
                continue;
            }
            if(flag==0 ){
                if(nums[i]<target[i]){
                    if(diff>=temp)
                        ans+=diff-temp;
                    // if(temp>0)
                    // temp=min(temp,diff);
                    // else temp=diff;

                }
                else {
                    ans+=diff;
                    // temp=diff;
                    flag=1;
                }
            }
            else{
                if(nums[i]>target[i]){
                    if(diff>=temp)
                        ans+=diff-temp;
                    // if(temp>0)
                    // temp=diff;
                }
                else{
                    ans+=diff;
                    // temp=diff;
                    flag=0;
                }

            }
            temp=diff;

        }
        if(mid>=ans) return true;
        return false;
    }
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long  low=0,n=nums.size(),high=0,ans;
        for(long long i=0;i<n;i++){
            high+=abs(nums[i]-target[i]);
        }
        while(low<=high){
            long long mid=low+(high-low)/2;
            if(pos(nums,target,mid)){
                ans=mid;
                high=mid-1;
            }
            else{
                low=mid+1;
            }
        }
        return ans;

    }
};
