class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans=0;
        int n=nums.size();
        int inc=0,incprev=0,dcr=0,dcrprev=0;
        for(int i=0;i<n;i++){
            if(nums[i]<=target[i]){
                int x=target[i]-nums[i];
                if(x>=inc){
                    ans+=x-inc;
                    inc=x;
                }
                else{
                    inc=x;
                }
                dcr=0;
            }
            else{
                int x=abs(target[i]-nums[i]);
                if(x>=dcr){
                    ans+=x-dcr;
                    dcr=x;
                }
                else{
                    dcr=x;
                }
                inc=0;
            }
        }
        return ans;
    }
};
