class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        dif = []
        for i in range(len(nums)):
            dif.append(nums[i]-target[i])
        dif.append(0)
        def sign(i):
            fsign = 0
            if dif[i] > 0:
                fsign = 1
            elif dif[i] < 0:
                fsign = -1

            ssign = 0
            if dif[i-1] > 0:
                ssign = 1
            elif dif[i-1] < 0:
                ssign = -1

            return fsign == ssign

        res = 0 
        for i in range(1,len(dif)):
            if sign(i):
                if abs(dif[i]) < abs(dif[i-1]):
                    res += abs(dif[i]-dif[i-1])

            else:
                res += abs(dif[i-1])
        return res
