class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        int n = nums.length;
        long[] ops = new long[n];

        for(int i = 0;i<n;i++){
            ops[i] = target[i]-nums[i];
            // System.out.println(ops[/i]);
        }


        long totalOp = 0;
        int l = 0;

        while (l<n){
            int r = l;
            boolean searchForPo = true;
            if(ops[r]<0){
                searchForPo = false;
            }

            boolean matchF = true;
            while (r<n && matchF) {
                if(searchForPo && ops[r]>=0){
                    r++;
                }else if(!searchForPo && ops[r]<0){
                    r++;
                }else{
                    matchF = false;
                }
            }

            totalOp += opsCount(ops, l, r-1, searchForPo);

            l = r;
        }

        return totalOp;
    }

    public long opsCount(long[] vals,int l,int r,boolean isPOs){
        if(!isPOs){
            for(int i = l;i<=r;i++){                
                vals[i] = vals[i]*-1;
            }
        }


        long totalOp = vals[l];
        long prefixOp = vals[l];

        for(int i = l+1;i<=r;i++){
            // System.out.print(vals[i]+" ");
            if(vals[i]<=prefixOp){
                prefixOp = vals[i];
            }else{
                long moreOp = vals[i]-prefixOp;
                prefixOp = moreOp+prefixOp;
                totalOp+=moreOp;
            }
        }
        // System.out.println("");
        return totalOp;
    }

}
