class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<int> dif(n,0);




        for(int i=0;i<n;i++){
            dif[i] = target[i]-nums[i];
        }


        // for(auto it:dif){
        //     cout<<it<<" ";
        // }
        // cout<<endl;
        long long cnt = abs(dif[0]);
        for(int i=1;i<n;i++){
            if(dif[i]<=0 && dif[i-1]<=0){
                cnt += max(0,dif[i-1]-dif[i]);
            }
            else if(dif[i]>=0 && dif[i-1]>=0){
                cnt += max(dif[i] - dif[i - 1], 0);
            }
            else{
                cnt += abs(dif[i]);
            }

            // cout<<cnt<<endl;
        }

        return cnt;





    }
};
