class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        for(int i = 0; i < nums.size(); i++){
            nums[i] -= target[i];
        }
        long long cur = 0, res = 0;
        for(int i = 0; i < nums.size(); i++){
            if(nums[i] >= 0 && cur <= 0){
                cur = nums[i];
                res += abs(nums[i]);
            } else if(nums[i] <= 0 && cur >= 0){
                cur = nums[i];
                res += abs(nums[i]);
            } else if (nums[i] >= 0 && cur >= 0) {
                // cur = 5, nums[i] = 3
                if(cur < nums[i]) res += nums[i] - cur;
                cur = nums[i];
            } else {
                if(cur > nums[i]) res += cur - nums[i];
                cur = nums[i];
            }
        }
        //         for(int i = 0; i < nums.size(); i++) cout << nums[i] << ' ';
        // cout << '\n';
        return res;

    }
};
