class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        pos = [[]]
        neg = [[]]
        for a, b in zip(nums, target):
            diff = a - b
            if diff > 0:
                if neg[-1]: neg.append([])
                pos[-1].append(diff)
            elif diff == 0:
                if pos[-1]: pos.append([])
                if neg[-1]: neg.append([])
            else:
                if pos[-1]: pos.append([])
                neg[-1].append(-diff)

        def find(arr):
            if not arr: return 0
            arr = [0] + arr + [0]
            ans = 0
            min_n = 0
            for i in range(1, len(arr)-1):
                if arr[i] >= arr[i-1] and arr[i] >= arr[i+1]:
                    ans += arr[i] - min_n
                    min_n = arr[i]
                else:
                    min_n = min(min_n, arr[i])
            return ans



        return sum(find(arr) for arr in pos) + sum(find(arr) for arr in neg)
