class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans = 0;
        vector<int> v;
        int n = nums.size();
        for(int i = 0; i < nums.size(); ++i){
            nums[i] = target[i] - nums[i];
        }
        if(nums.size() < 2)
            return abs(nums[0]);

        for(auto i : nums)
            cout<<i<<" ";
        cout<<endl;
        int i = 0;

        while(i < n){
            // printf("%d %d\n",i, nums[i]);
            cout<<i<<endl;
            if(nums[i] > 0){
                if(v.size()){
                    ans += abs(v.back());
                    v.clear();
                }
                while(i < n && nums[i] > 0){
                    if(!v.size() || nums[i] >= v.back())
                        v.push_back(nums[i]);
                    else{
                        int mx = nums[i];
                        while(v.size() && v.back() >= nums[i]){
                            if(v.back() > mx){
                                ans += v.back() - mx;
                                mx = v.back();
                            }
                            v.pop_back();
                        }
                        v.push_back(nums[i]);
                    }
                    ++i;
                }
            }else if(nums[i] < 0){
                if(v.size()){
                    ans += v.back();
                    v.clear();
                }

                while(i < n && nums[i] < 0){
                    if(!v.size() || nums[i] <= v.back()){
                        v.push_back(nums[i]);
                    }else{
                        int mn = nums[i];
                        while(v.size() && v.back() <= nums[i]){
                            if(v.back() < mn){
                                ans += abs(v.back() - mn);
                                mn = v.back();
                            }
                                v.pop_back();
                        }
                        v.push_back(nums[i]);
                    }
                    ++i;
                }
            }else{
                ++i;
                if(v.size()){
                    ans += abs(v.back());
                    v.clear();
                }
            }
        }
        if(v.size())
            ans += abs(v.back());
        return ans;
    }
};
