impl Solution {
    pub fn minimum_operations(nums: Vec<i32>, target: Vec<i32>) -> i64 {
        let mut muffi: Vec<i32> = nums.iter()
            .zip(target.iter())
            .map(|(n, t)| t - n)
            .collect();

        let mut result: i64 = 0;
        let mut csfis: i32 = 0;

        for i in 0..muffi.len() {
            if i > 0 && 
                ((muffi[i - 1] > 0 && muffi[i] > 0) || 
                 (muffi[i - 1] < 0 && muffi[i] < 0)) {
                let ans = (muffi[i].abs() - csfis.abs()) as i64;
                if ans > 0 {
                    result += ans;
                }
                csfis = muffi[i];
            } else {
                result += muffi[i].abs() as i64;
                csfis = muffi[i];
            }
        }

        result
    }
}
