class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<int> D(n);

        // Calculate the absolute differences between nums and target
        for (int i = 0; i < n; i++) {
            D[i] = nums[i] - target[i];
        }
        long long ans = 0;
        long long cs = 0;

        for (int i = 0; i < n; ++i) {
            if (!i or (D[i - 1] < 0 and D[i] < 0) or  (D[i - 1] > 0 and D[i] > 0)) {
                int op = abs(D[i]) - abs(cs);
                ans += op>0 ?op:0;
            } else {
                ans += abs(D[i]);
            }
            cs=D[i];
        }

        return ans;
    }
};
