class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int dist[nums.size()];
        long long max_ans=0;
        for(int i=0;i<nums.size();i++)
        {
            max_ans+=abs(target[i]-nums[i]);
            dist[i]=target[i]-nums[i];
        }
        long long final_ans=0;
        int ans=0,res=0,f=0,last=-1;
        for(int i=0;i<nums.size();i++)
        {
            if(dist[i]>0)
            {
                f=0;
                if(last==-1)
                {
                    last=dist[i];
                    ans+=last;
                }
                else
                {
                    ans+=max(dist[i]-last,0);
                    last=dist[i];
                }
            }
            else
            {
                if(f==0)
                {
                    last=-1;
                    final_ans+=ans;
                    ans=0;
                    f=1;
                }
            }
        }
        if(f==0)
            final_ans+=ans;
        last=1;
        ans=0;
        f=0;
        for(int i=0;i<nums.size();i++)
        {
            if(dist[i]<0)
            {
                f=0;
                if(last==1)
                {
                    last=dist[i];
                    ans+=last;
                }
                else
                {
                    ans+=min(dist[i]-last,0);
                    last=dist[i];
                }
            }
            else
            {
                if(f==0)
                {
                    last=1;
                    final_ans+=abs(ans);
                    ans=0;
                    f=1;
                }
            }
        }
        if(f==0)
            final_ans+=abs(ans);
        return min(max_ans,final_ans);
    }
};
