class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        int n = target.length;
        for (int i = 0; i < n; i++) {
            target[i] -= nums[i];
        }
//        System.out.println("target: " + Arrays.toString(target));
        int mn = Math.min(target[0], target[n - 1]);
        int mx = Math.max(target[0], target[n - 1]);
        if (mn < 0) {
            for (int i = 0; i < n; i++) {
                target[i] -= mn;
            }
        }
//        System.out.println("target: " + Arrays.toString(target));
        long ans = target[0];
        for (int i = 1; i < n; i++) {
            ans += Math.max(target[i] - target[i - 1], 0);
        }
        if (mx < 0) ans -= mx;
        return ans;
    }
}
