int abs(int a){
    return a > 0 ? a : 0 - a;
}
int sgn(int a){
    if(a > 0){
        return 1;
    }
    else if(a < 0){
        return -1;
    }
    else{
        return 0;
    }
}

long long minimumOperations(int* nums, int numsSize, int* target, int targetSize) {
    int status = 0, i = 0;
    int diff1 = 0, diff2 = 0;
    long long result = 0;
    if(numsSize == 1){
        return abs(nums[0] - target[0]); 
    }
    else if(numsSize == 2){
        diff1 = nums[0] - target[0];
        diff2 = nums[1] - target[1];
        if(sgn(diff1)*sgn(diff2) < 0){
            return abs(diff1) + abs(diff2);
        }
        else{
            return fmax(abs(diff1),abs(diff2));
        }
    }
    else{ 
        while(i < numsSize){
            diff1 = diff2;
            diff2 = nums[i] - target[i];
            if(sgn(diff1) * sgn(diff2) < 0){
                if(abs(status) == 2){
                    result += abs(diff1);
                }
                status = 2 * sgn(diff2);
            }
            else if(diff2 == 0){
                if(abs(status) == 2){
                    result += abs(diff1);
                }
                status = 0;
                i++;
                continue;
            }
            else if(diff1 >= 0 && diff2 > 0){
                if(status == 0){
                    status = 2;
                }
                else if(status == 2 && diff2 < diff1){
                    status = 1;
                    result += diff1;
                }
                else if(status == 1 && diff2 > diff1){
                    status = 2;
                    result -= diff1;
                }
            }
            else if(diff1 <= 0 && diff2 < 0){
                if(status == 0){
                    status = -2;
                }
                else if(status == -2 && diff2 > diff1){
                    status = -1;
                    result -= diff1;
                }
                else if(status == -1 && diff2 < diff1){
                    status = -2;
                    result += diff1;
                }
            }
            i++;
        }    
    }
    if(abs(status) == 2){
        result += abs(diff2);
    }
    return result;

}
