class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        for (int i = 0; i < n; i++) {
            target[i] -= nums[i];
        }
        vector<int> b(n);
        b[0] = target[0];
        for (int i = 1; i < n; i++) {
            b[i] = target[i] - target[i - 1];
        }
        long long pos = 0, neg = 0;
        for (int i = 0; i < n; i++) {
            if (b[i] > 0) {
                pos += b[i];
            } else {
                neg += -b[i];
            }
        }
        return max(pos, neg);
    }
};
