class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();

        vector<int> diff(n);

        for(int i=0;i<n;i++) diff[i]=target[i]-nums[i];

        long long  ans =0;
        ans+=abs(0-diff[0]);

        for(int i=1;i<n;i++){
            if(diff[i]<0){
                if(diff[i]<diff[i-1] && diff[i-1]<0) ans+=(diff[i-1]-diff[i]);
                if(diff[i-1]>=0) ans+=(0- diff[i]);

            }
            else{
                if(diff[i-1]>0){
                    if(diff[i]>diff[i-1]) ans+=(diff[i]-diff[i-1]);
                }
                else ans+=diff[i];
            }

        }
        return ans;


    }
};
