class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        nums = [nums[i] - target[i] for i in range(len(nums))]
        curr = 0
        pos = True
        res = 0
        for el in nums:
            if el >= 0 and pos:
                res += max(el - curr, 0)
            elif el >= 0 and not pos:
                res += el
                pos = True
            elif el < 0 and pos:
                pos = False
                res += abs(el)
            elif el < 0 and not pos:
                res += max(0, abs(el) - abs(curr))
            curr = el
        return res
