class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n = len(nums)
        ans = mn = mx = 0
        flag = (target[0] - nums[0] >= 0)
        for i, (x, y) in enumerate(zip(nums, target)):
            k = y - x
            print(k, flag)
            if flag:
                if k > 0:
                    if k > mx:
                        ans += k - mx
                        mx = k
                    else:
                        mx = min(mx, k)
                else:
                    mx = k
                    ans -= k
            else:
                if k < 0:
                    if k < mx:
                        ans += mx - k
                        mx = k
                    else:
                        mx = max(k, mx)
                else:
                    mx = k
                    ans += k
            flag = (k >= 0)

        return ans
