class Solution {
public:
    long long minimumOperations(std::vector<int>& nums, std::vector<int>& target) {
        vector<int> diff;
        int n = nums.size();

        for (int i = 0; i < n; ++i) {
            diff.push_back(target[i] - nums[i]);
        }

        long long ans = 0;
        int seg = 0;

        for (size_t i = 0; i < n; ++i) {
            if (i == 0 || (diff[i - 1] > 0 && diff[i] > 0) || (diff[i - 1] < 0 && diff[i] < 0)) {
                int a= abs(diff[i]) - abs(seg);
                if (a > 0) {
                    ans += a;
                }
                seg = diff[i];
            } else {
                ans += std::abs(diff[i]);
                seg = diff[i];
            }
        }

        return ans;
    }
};
