class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        long long cnt=0;
        vector<int>sub(n,0);
        for(int i=0;i<n;i++){
            sub[i]=nums[i]-target[i];
        }
        int i=0;
        while(i<n){

            int prev=0,curr=sub[i];
            while(i<n&&sub[i]>0){
                curr=sub[i];
                if(prev<=curr)
                cnt+=curr-prev;

                prev=sub[i];
                i++;
            }
            while(i<n&&sub[i]==0)
            i++;
            prev=0,curr=sub[i];
            while(i<n&&sub[i]<0){
                curr=abs(sub[i]);
                if(prev<=curr)
                cnt+=abs(curr)-abs(prev);

                prev=abs(sub[i]);
                i++;
            }
            while(i<n&&sub[i]==0)
            i++;
        }
        return cnt;
    }
};
