class Solution {
public:
    long long minimumOperations(vector<int>& v, vector<int>& t) {
        int n=v.size();
        int last=abs(t[0]-v[0]);
        long long ct=abs(last);
        for(int i=1;i<n;i++){
            if(t[i]>v[i] && t[i-1]>v[i-1]){
             ct+=max(0,abs(t[i]-v[i])-last);   
             last=t[i]-v[i];
            }
            else if(t[i]>v[i] && t[i-1]<v[i-1]){
                ct+=max(0,abs(t[i]-v[i]));
                last=t[i]-v[i];
            }
            else if(t[i]>v[i] && last==0){
                ct+=t[i]-v[i];
                last=t[i]-v[i];
            }
            else if(t[i]<v[i] && t[i-1]<v[i-1]){
                ct+=max(0,abs(v[i]-t[i])-last);
                last=v[i]-t[i];
            }
            else if(t[i]<v[i] && t[i-1]>v[i-1]){
                ct+=abs(v[i]-t[i]);
                last=v[i]-t[i];
            }
            else if(t[i]<v[i]){
                ct+=v[i]-t[i];
                last=v[i]-t[i];
            }
            else if(t[i]==v[i]){
                last=0;
            }
        }
        return ct;

    }
};
