class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        for(int i=0;i<n;i++){
            nums[i]=target[i]-nums[i];
        }
        long long ans=abs(nums[0]),curr=nums[0];
        for(int i=1;i<n;i++){
            if((nums[i-1]>=0&&nums[i]>=0)||(nums[i-1]<0&&nums[i]<0)){
                if(abs(nums[i])>abs(curr)){
                    ans+=abs(nums[i])-abs(curr);
                }
                curr=nums[i];
            }
            else{
                ans+=abs(nums[i]);
                curr=nums[i];
            }
        }
        return ans;
        return 0;
    }
};
