class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        long long pre = 0;
        long long ans=0;
        for(int i=0;i<n;++i){
            nums[i]-=target[i];
            //cout<<nums[i]<<','<<pre<<endl;
            if(pre*nums[i]<0) pre=0;
            if(abs(pre)<abs(nums[i])){
                ans+=abs(nums[i]-pre);
            }
            pre=nums[i];
        }
        return ans;
    }
};
