class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
 int n = nums.size();
        vector<long long> v(n);

        for (int i = 0; i < n; ++i) {
            v[i] = target[i] - nums[i];
        }

        long long op = 0;
        long long d = 0;

        for (int i = 0; i < n; ++i) {
            if (i == 0 || (v[i - 1] > 0 && v[i] > 0) || (v[i - 1] < 0 && v[i] < 0)) {
                long long op1 = abs(v[i]) - abs(d);
                if (op1 > 0) {
                    op += op1;
                }
                d = v[i];
            } else {
                op += abs(v[i]);
                d = v[i];
            }
        }

        return op;
    }
};
