class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        int n = nums.length;
        int d[] = new int[n];
        long res = 0;
        for(int i=0;i<n;i++)
            d[i] = target[i]-nums[i];
        int i = 0;
        while(i<n&&d[i]==0)
            i++;
        if(i==n) return 0l;
        boolean pos = d[i]>0;
        res = Math.abs(d[i++]);
        while(i<n){
            if(pos){
                if(d[i]<0){
                    pos = false;
                    res+=0l-d[i];
                }
                else if(d[i]>d[i-1])
                    res+=0l+d[i]-d[i-1];
            } else {
                if(d[i]>0){
                    pos = true;
                    res+=0l+d[i];
                }
                else if(d[i-1]>d[i])
                    res+=0l+d[i-1]-d[i];
            }
            i++;
        }
        return res;        
    }
}
