typedef long long ll;
class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        ll res=0;
        ll n=nums.size();
        vector<ll>s(n+1,0);
        ll x=0,y=0;
         for(int i=0;i<n;i++){
             s[i+1]=target[i]-nums[i];
             cout<<s[i+1];
        }
        for(int i=1;i<=n;i++){

            ll u=s[i]-s[i-1];
            if(u>0) x+=u;
            else y-=u;
        }
        return max(x,y);
    }
};
