bool same_sign(int x,int y){
    if(y==0)
        return 1;
    if(x==0)
        return 0;
    if(x>0 && y<0)
        return 0;
    if(x<0 && y>0)
        return 0;
    return 1;
}


ll solve(vector<int> v){
    if(v.size()==0)
        return 0;
    //trace(v);
    ll ans = 0LL;
    int carry = 0;
    ans = (ll)v[0];
    carry = v[0];
    for(int i=1;i<v.size();i++){
        int x = v[i];
        if(x<=carry){
            carry = x;
        }
        else{
            ans += (ll)(x-carry);
            carry = x;
        }
    }
    return ans;
}

class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        ll ans = 0LL;
        int n = nums.size();
        vector<int> v;
        for(int i=0;i<n;i++)
            v.pb(target[i]-nums[i]);
        //trace(v);
        vector<int> vv;
        if(v[0]!=0)
            vv.push_back(abs(v[0]));
        for(int i=1;i<n;i++){
            if(same_sign(v[i],v[i-1])){
                vv.push_back(abs(v[i]));
            }
            else{
                ans += solve(vv);
                vv.clear();
                if(v[i]!=0){
                    vv.pb(abs(v[i]));
                }
            }
        }
        ans += solve(vv);
        return ans;
    }
};
