class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        def f(a):
            pre = 0
            res = []
            for x in a:
                res.append(x - pre)
                pre = x
            return res
        s = res = 0
        for x, y in zip(f(nums), f(target)):
            t = x - y
            if abs(s + t) == abs(s) + abs(t):
                res += abs(t)
            elif abs(t) > abs(s): res += abs(t) - abs(s)
            s += t
        return res
