class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<int> diffs(nums.size());

        for (int i = 0; i < nums.size(); ++i) {
            diffs[i] = target[i] - nums[i];
        }

        bool positive = diffs[0] > 0;
        int current = abs(diffs[0]);
        long long ops = 0;

        for (int i = 1; i < diffs.size(); ++i) {
            if (diffs[i] > 0 != positive) {
                ops += (long long)current;

                positive = diffs[i] > 0;
                current = abs(diffs[i]);
            } else {
                if (abs(diffs[i]) < abs(current)) {
                    ops += abs(current) - abs(diffs[i]);
                }

                current = abs(diffs[i]);
            }
        }

        ops += (long long)current;

        return ops;
    }
};
