class Solution {
public:
    long long minimumOperations(vector<int>& s, vector<int>& k) {
        int n=s.size();
        long long ans=0,prev=0;
        for(int i=0;i<n;i++)
        {
            if(s[i]==k[i])prev=0;
            else if(s[i]<k[i])
            {
                long long diff=s[i]-k[i];
                if(prev>=0)ans-=diff;
                else if(diff<prev)
                {
                    ans+=(prev-diff);
                }
                prev=diff;
            }
            else
            {
                long long diff=s[i]-k[i];
                if(prev<=0)ans+=diff;
                else if(diff>prev)
                {
                    ans+=(diff-prev);
                }
                prev=diff;
            }
        }
        return ans;
    }
};
